// Operador - Soma:
function sumOperator(num1, num2) {
    resultado = num1 + num2;
    console.log("A soma é......: " + resultado);
}
sumOperator(10, 2);
// Operador - Subtração:
function subOperator(num1, num2) {
    resultado = num1 - num2;
    console.log("A subtração é......: " + resultado);
}
subOperator(10, 2);
// Operador - Multiplicação:
function multiOperator(num1, num2) {
    resultado = num1 * num2;
    console.log("A multiplicação é......: " + resultado);
}
multiOperator(10, 2);
// Operador - Divisão:
function divideOperator(num1, num2) {
    resultado = num1 / num2;
    console.log("A divisão é......: " + resultado);
}
divideOperator(10, 2);
// Operador - Módulo:
function modOperator(num1, num2) {
    resultado = num1 % num2;
    console.log("O módulo é......: " + resultado);
}
modOperator(10, 2);
// Operador - Incremento:
var num1 = 17;
num1++;
console.log("O incremento é.....: " + num1);
// Operador - Decremento:
var num2 = 15;
num2--;
console.log("O decremento é......: " + num2);
